document.addEventListener("DOMContentLoaded",e=>{let t=[].slice.call(document.querySelectorAll("img.lazyLoad"));if("IntersectionObserver"in window&&"IntersectionObserverEntry"in window&&"intersectionRatio"in window.IntersectionObserverEntry.prototype){let e=new IntersectionObserver((t,r)=>{t.forEach(t=>{if(t.isIntersecting){let r=t.target;r.srcset=r.dataset.srcset,r.src=r.dataset.src,r.classList.remove("lazyLoad"),e.unobserve(r)}})});t.forEach(t=>{e.observe(t)})}});